import React, { useState } from "react";
import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useTranslation } from "react-i18next";
import Home from "./components/home/Home";
import About from "./components/about/About";
import Contact from "./components/contact/Contact";
import AllProducts from "./components/products/AllProducts";
import Services from "./components/services/Services";

// Contains the value and text for the options
const languages = [
  { value: "", text: "Options" },
  { value: "en", text: "English" },
  { value: "tr", text: "Turkish" },
  { value: "ar", text: "Arabic" },
  { value: "az", text: "Azerbaijani" },
  { value: "de", text: "German" },
  { value: "ku", text: "Kurdish" },
];

function App() {
  const { t } = useTranslation();
  const [language, setLanguage] = useState("tr");

  // this function use query to change language
  const handleLanguage = (e) => {
    setLanguage(e.target.value);
    let local = "http://localhost:3000/";
    window.location.replace(local + "?lang=" + e.target.value);
  };

  return (
    <>
      <div className="App">
        {/* // we are showing the value by using the // keys we have created in the
        website */}
        <h1>{t("welcome")}</h1>
        {/* <label>{t("choose")}</label> */}
        <select value={language} onChange={handleLanguage}>
          {languages.map((item) => {
            return (
              <option key={item.value} value={item.value}>
                {item.text}
              </option>
            );
          })}
        </select>
      </div>
      <Router>
        <Routes>
          <Route path="" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/products" element={<AllProducts />} />
          <Route path="/services" element={<Services />} />
          <Route path="/annoucement" element={<Announcement />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </Router>
    </>
  );
}

export default App;
